changeSet.sql:
CREATE TABLESPACE BCDSS_DEV_TS DATAFILE 'bcdss_dev_01.dbf' SIZE 16M
AUTOEXTEND ON NEXT 8M MAXSIZE UNLIMITED
LOGGING
ONLINE
EXTENT MANAGEMENT LOCAL AUTOALLOCATE
BLOCKSIZE 8K
SEGMENT SPACE MANAGEMENT AUTO
FLASHBACK ON;

CREATE USER BCDSS_DEV
IDENTIFIED BY "bcdss_dev"
DEFAULT TABLESPACE BCDSS_DEV_TS
TEMPORARY TABLESPACE TEMP
ACCOUNT UNLOCK;

ALTER USER BCDSS_DEV DEFAULT ROLE ALL;
GRANT UNLIMITED TABLESPACE TO BCDSS_DEV;
ALTER USER BCDSS_DEV QUOTA UNLIMITED ON bcdss_dev_ts;
GRANT EXECUTE ON SYS.DBMS_LOB TO BCDSS_DEV WITH GRANT OPTION;
